#!/usr/bin/env node
var inquirer = require('inquirer');

inquirer.prompt([
	{
      type: 'input',
      name: 'pageName',
      message: 'Input the page name',
      default: 'index'
    },
    {
      type: 'confirm',
      name: 'needConfig',
      message: 'Do you need a configuration file',
      default: false
    }

	])
.then(opition=>{
	console.log(opition)
})
.catch(err=>{
	console.log(err)
})

